export = com;
export as namespace com;

declare namespace com.example.myapplication.jsbindings {

      class JsDependencyContainer {
        constructor(serverProtocol: string, serverUrl: string, serverPort: number)

        createGame: JsUseCaseWithParamsPromise<CreateGameUseCaseParams, GameEntity>

      }
}

declare interface JsUseCaseWithParamsPromise<P, T> {
  buildAction(params: P): Promise<T>
}

declare interface CreateGameUseCaseParams {
  name: string
  availableModes: String[]
  image: String
  icon: string
}

declare interface GameEntity {
  name: string
  availableModes: string[]
  image: string
  icon: string
}
